import { useState } from "react";

import Card from "./UI/Card";
import Button from "./UI/Button";

import styles from "./DifficultySelector.module.css";

const DifficultySelector = (props) => {
  const [difficulty, setDifficulty] = useState(null);

  const handleClick = (event) => {
      setDifficulty(event);
      console.log(event.firstChild.data);
  };

  return (
    <Card className={styles.difficulty}>
      <Button className={styles.easy} onClick={handleClick}>
        Easy
      </Button>
      <Button className={styles.medium}>Medium</Button>
      <Button className={styles.hard}>Hard</Button>
    </Card>
  );
};

export default DifficultySelector;
